import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [userInput, setUserInput] = useState('');
  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    if (!userInput.trim()) return;
    setLoading(true);
    try {
      const res = await axios.post('http://127.0.0.1:8000/query', { user_input: userInput });
      setResponse(res.data);
    } catch (error) {
      console.error(error);
      setResponse({ error: 'Something went wrong. Please try again.' });
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <h1>ðŸ©º First-Aid Chatbot</h1>

      <textarea
        className="input-field"
        rows="5"
        placeholder="Describe symptoms here..."
        value={userInput}
        onChange={(e) => setUserInput(e.target.value)}
      />

      <button className="submit-button" onClick={handleSubmit} disabled={loading}>
        {loading ? 'Processing...' : 'Submit'}
      </button>

      {response && (
        <div className="response-card">
          {response.condition && (
            <>
              <h2>Condition:</h2>
              <p>{response.condition}</p>
            </>
          )}
          {response.answer && (
            <>
              <h2>Response:</h2>
              <p>{response.answer}</p>
            </>
          )}
          {response.error && <p className="error">{response.error}</p>}
        </div>
      )}
    </div>
  );
}
import Footer from './Footer';

// Inside your App component return:
<>
